function cleanTextPreserveNewlines(input) {
    // Step 1: Remove references like [number]
    let text = input.replace(/\[\d+\]/g, '');

    // Step 2: Remove unwanted symbols but preserve newlines
    // Keep letters, numbers, spaces, punctuation, and \n
  text = text.replace(/[^\w\s.,!?'\n#*()-]/g, '');

    // Step 3: Collapse multiple spaces (but not newlines)
    text = text.replace(/[ \t]+/g, ' ');

    // Step 4: Reduce repeated punctuation
    text = text.replace(/([.!?])\1+/g, '$1'); // !!! or ??? → ! or ?
    text = text.replace(/(\.{2,})/g, '.');     // ... → .

    // Step 5: Trim spaces at the start/end of each line
    text = text.split('\n').map(line => line.trim()).join('\n');

    return text;
}

let item = $input.first();
let text = cleanTextPreserveNewlines(item.json.text);
item.json.text = text;
return [item];
